import React, {FunctionComponent, createContext, useState, useEffect, useContext} from 'react'
import { BuildMessage } from './Message'
import Token from '../models/Token'

// @ts-ignore
import socketIOClient from 'socket.io-client'

export const SocketIOContext = createContext({ url: 'http://127.0.0.1:7000' });

const Balance:FunctionComponent = () => {
    const { url } = useContext(SocketIOContext)
    const socket = socketIOClient(url);

    const [token, setToken] = useState(new Token(''))
    const [inputValue, setInputValue] = useState()
    const [message, setMessage] = useState()

    useEffect(() => {
        if (token.valid) {
            setToken(token)
            socket.emit("message", token.value);

            socket.on("message", (data: any) => console.log(data));
        } else {
            setMessage({
                show: true,
                message: 'Wrong token',
                type: 'error'
            })
        }
    }, [token])

    const handleInputChange = (e:React.FormEvent<HTMLInputElement>) => {
        setInputValue(e.currentTarget.value)
    }

    const checkToken = () => {
        token.value = inputValue
        token.validate()

    }

    const cleanToken = () => {
        setInputValue('')
        setToken(new Token())

        socket.disconnect()
    }

    return (
        <>
            <input type="text" value={inputValue} onChange={handleInputChange}/>

            <div className="btn-group">
                <button className={'btn btn-primary waves-effect waves-light'} onClick={checkToken}>Lookup</button>
                <button className={'btn btn-info waves-effect waves-light'} onClick={cleanToken}>Clean</button>
            </div>

            {BuildMessage(message)}
        </>
    )
}

export default Balance
