// @ts-ignore
import socketIOClient from 'socket.io-client'

export default class Socket {
    socketURL: string
    socketIO: socketIOClient

    constructor(socketURL: string) {
        this.socketURL = socketURL;
        this.connect()
    }

    connect() {
        this.socketIO = socketIOClient(this.socketURL)
    }

    disconnect() {
        this.socketIO.disconnect()
    }

    emmit(channel: string, message: any, callback: Function) {
        this.socketIO.emit(channel, message);

        this.socketIO.on(channel, callback)
    }

}
