import React, { FunctionComponent, useState } from 'react'
import { Message } from './Message'
import Token from '../models/Token'

const Balance:FunctionComponent = () => {
    const [token, setToken] = useState(new Token())
    const [inputValue, setInputValue] = useState()
    const [tokenMessage, setTokenMessage] = useState(false)

    const handleInputChange = (e:React.FormEvent<HTMLInputElement>) => {
        setInputValue(e.currentTarget.value)
    }

    const checkToken = () => {
        token.value = inputValue
        token.validate()

        setTokenMessage(!token.valid)
        setToken(token)
    }

    const cleanToken = () => {
        setInputValue('')
        setToken(new Token())
    }

    return (
        <>
            <input type="text" value={inputValue} onChange={handleInputChange}/>

            <button onClick={checkToken}>Lookup</button>
            <button onClick={cleanToken}>Clean</button>

            <Message show={tokenMessage} message={'The entry token is not a valid ECR20 token'} type={'error'}/>
        </>
    )
}

export default Balance
